mapper.select:
SELECT 

FROM pro_audience_city a

WHERE a.id = #{id}

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM pro_audience_city a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE pro_audience_city SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
